function tabActive(t,e,a){const l=document.querySelectorAll(""+t),o=document.querySelectorAll(""+e);l.forEach(c=>{c.addEventListener("click",()=>{var t=c.getAttribute(""+a);const e=document.getElementById(t);l.forEach(t=>{t.classList.remove("--active")}),o.forEach(t=>{t.classList.remove("--active")}),c.classList.add("--active"),e.classList.add("--active")})})}function accordion(t){const e=document.querySelectorAll(""+t);e.forEach(e=>{e.addEventListener("click",()=>{e.classList.toggle("--active");const t=e.nextElementSibling;t.style.maxHeight?t.style.maxHeight=null:t.style.maxHeight=t.scrollHeight+"px"})})}tabActive(".panel-btn",".panel","data-panel"),tabActive(".support-btn",".support-box","data-panel"),accordion(".support-accordion");